<script>import { onDestroy, onMount, setContext, getContext } from "svelte";
import { Circle, Map } from "leaflet";
import { Compare } from "./utils/index";
export let latLng;
export let options = { radius: 100 };
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let circle;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = circle;
onMount(() => {
  circle = new Circle(latLng, options);
  compare = new Compare(circle, $$props);
  ready = true;
});
$: if (map) {
  if (circle) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(circle);
    } else {
      map.addLayer(circle);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  circle?.remove();
  circle = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => circle });
</script>

{#if ready}
	<slot />
{/if}
