<script>import { onMount, onDestroy, getContext, setContext } from "svelte";
import { CircleMarker, Map } from "leaflet";
import { Compare } from "./utils/index";
export let latLng;
export let options = { radius: 10 };
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let circleMarker;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = circleMarker;
onMount(() => {
  circleMarker = new CircleMarker(latLng, options);
  compare = new Compare(circleMarker, $$props);
  ready = true;
});
$: if (map) {
  if (circleMarker) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(circleMarker);
    } else {
      map.addLayer(circleMarker);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  circleMarker?.remove();
  circleMarker = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => circleMarker });
</script>

{#if ready}
	<slot />
{/if}
