<script>import { onMount, onDestroy, getContext, setContext } from "svelte";
import { Map, control } from "leaflet";
import { Compare } from "./utils/index";
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap } = parentContext;
let controlLayers;
let ready = false;
let compare;
$: map = getMap?.();
$: instance = controlLayers;
onMount(() => {
  controlLayers = control.layers(void 0, void 0, options);
  compare = new Compare(controlLayers, $$props);
  ready = true;
});
$: if (map) {
  if (controlLayers) {
    compare.updateProps($$props);
    controlLayers.addTo(map);
    compare.storeProps($$props);
  }
}
function reset() {
  controlLayers?.remove();
  controlLayers = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getControl: () => controlLayers });
</script>

{#if ready}
	<slot />
{/if}
