<script>import { onMount, onDestroy, getContext, setContext } from "svelte";
import { Map, ImageOverlay } from "leaflet";
import { Compare } from "./utils/index";
export let url;
export let bounds;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let imageOverlay;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = imageOverlay;
onMount(() => {
  imageOverlay = new ImageOverlay(url, bounds, options);
  compare = new Compare(imageOverlay, $$props);
});
$: if (map) {
  if (imageOverlay) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(imageOverlay);
    } else {
      map.addLayer(imageOverlay);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  imageOverlay?.remove();
  imageOverlay = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => imageOverlay });
</script>
