<script>import { onMount, onDestroy, setContext, getContext } from "svelte";
import { Map, Marker, Icon } from "leaflet";
import { Compare } from "./utils/index";
export let latLng;
export let options = {
  icon: new Icon.Default()
};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let marker;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = marker;
onMount(() => {
  marker = new Marker(latLng, options);
  compare = new Compare(marker, $$props);
  ready = true;
});
$: if (map) {
  if (marker) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(marker);
    } else {
      map.addLayer(marker);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  marker?.remove();
  marker = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => marker });
</script>

{#if ready}
	<slot />
{/if}
