<script>import { onMount, onDestroy, getContext } from "svelte";
import { Map, Popup } from "leaflet";
import { Compare } from "./utils/index";
export let latLng = void 0;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getOverlay } = parentContext;
let popup;
let htmlElement;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getOverlay?.();
$: instance = popup;
onMount(() => {
  let mergeOptions = {
    ...options
  };
  if (htmlElement) {
    mergeOptions = {
      ...mergeOptions,
      content: htmlElement
    };
  }
  if (!latLng && layer) {
    popup = new Popup(mergeOptions, layer);
  } else if (latLng) {
    popup = new Popup(latLng, mergeOptions);
  } else {
    throw new Error("Prop latLng is required.");
  }
  compare = new Compare(popup, { ...$$props, options: mergeOptions });
  ready = true;
});
$: if (map) {
  if (popup) {
    compare.updateProps($$props);
    if (!layer) {
      popup.openOn(map);
    } else {
      let popupContent = popup.options.content || "";
      layer.bindPopup(popupContent);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  popup?.remove();
  popup = void 0;
}
onDestroy(() => {
  reset();
});
</script>

{#if $$slots.default}
	<div style="display: none">
		<div bind:this={htmlElement} {...$$restProps}>
			{#if ready}
				<slot />
			{/if}
		</div>
	</div>
{/if}
