<script>import { onDestroy, onMount, setContext, getContext } from "svelte";
import { Map, SVGOverlay } from "leaflet";
import { Compare } from "./utils/index";
export let bounds;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let svgOverlay;
let svgElement;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = svgOverlay;
onMount(() => {
  if (svgElement) {
    svgOverlay = new SVGOverlay(svgElement, bounds, options);
  } else {
    throw new Error("SVG Element Required!");
  }
  compare = new Compare(svgOverlay, options);
  ready = true;
});
$: if (map) {
  if (svgOverlay) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(svgOverlay);
    } else {
      map.addLayer(svgOverlay);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  svgOverlay?.remove();
  svgOverlay = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => svgOverlay });
</script>

<svg bind:this={svgElement} xmlns="http://www.w3.org/2000/svg" {...$$restProps}>
	{#if ready}
		<slot />
	{/if}
</svg>
