<script>import { onMount, onDestroy, getContext } from "svelte";
import { Map, TileLayer } from "leaflet";
import { setControlLayer, Compare } from "./utils/index";
export let url;
export let options = {};
export let name = "";
export let checked = false;
export let instance = void 0;
export let layerType = void 0;
let parentContext = getContext(Map);
const { getMap, getControl } = parentContext;
let tileLayer;
let compare;
$: map = getMap?.();
$: controlLayers = getControl?.();
$: instance = tileLayer;
onMount(() => {
  tileLayer = new TileLayer(url, options);
  compare = new Compare(tileLayer, $$props);
});
$: if (map) {
  if (tileLayer) {
    compare.updateProps($$props);
    if (controlLayers) {
      if (!name) {
        console.warn("Name is required in ControlLayers");
      } else {
        setControlLayer({
          layer: tileLayer,
          name,
          controlLayers,
          layerType,
          map,
          checked
        });
      }
      controlLayers.addTo(map);
    } else {
      map.addLayer(tileLayer);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  tileLayer?.remove();
  tileLayer = void 0;
}
onDestroy(() => {
  reset();
});
</script>
