<script>import { onMount, onDestroy, getContext, setContext } from "svelte";
import { Map, VideoOverlay } from "leaflet";
import { Compare } from "./utils/index";
export let url = "";
export let bounds;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let videoOverlay;
let htmlVideoElement;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = videoOverlay;
onMount(() => {
  let mergeVideo = htmlVideoElement || url;
  videoOverlay = new VideoOverlay(mergeVideo, bounds, options);
  compare = new Compare(videoOverlay, $$props);
  ready = true;
});
$: if (map) {
  if (videoOverlay) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(videoOverlay);
    } else {
      map.addLayer(videoOverlay);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  videoOverlay?.remove();
  videoOverlay = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => videoOverlay });
</script>

{#if $$slots.default}
	<!-- svelte-ignore a11y-media-has-caption -->
	<video bind:this={htmlVideoElement} {...$$restProps}>
		{#if ready}
			<slot />
		{/if}
	</video>
{/if}
