<script>import { onMount, onDestroy, getContext } from "svelte";
import { DivIcon, Map, Marker } from "leaflet";
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getOverlay } = parentContext;
let divIcon;
let htmlElement;
let ready = false;
$: map = getMap?.();
$: layer = getOverlay?.();
$: instance = divIcon;
onMount(() => {
  let mergeOptions = {
    ...options
  };
  if (htmlElement) {
    mergeOptions = {
      ...mergeOptions,
      html: htmlElement
      // override options
    };
  }
  divIcon = new DivIcon(mergeOptions);
  ready = true;
});
$: if (map) {
  if (divIcon) {
    if (layer && layer instanceof Marker) {
      layer.setIcon(divIcon);
    } else {
      console.warn("DivIcon should bind Marker.");
    }
  }
}
function reset() {
  divIcon?.remove?.();
  divIcon = void 0;
}
onDestroy(() => {
  reset();
});
</script>

{#if $$slots.default}
	<div style="display: none">
		<div bind:this={htmlElement} {...$$restProps}>
			{#if ready}
				<slot />
			{/if}
		</div>
	</div>
{/if}
