<script>import { onMount, onDestroy, getContext, setContext } from "svelte";
import { Map, GeoJSON } from "leaflet";
export let json = null;
export let options = null;
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap } = parentContext;
let geoJSON;
onMount(() => {
  geoJSON = new GeoJSON(json, options);
});
$: map = getMap?.();
$: instance = geoJSON;
$: if (map) {
  if (geoJSON) {
    map.addLayer(geoJSON);
  }
}
function reset() {
  geoJSON?.remove();
  geoJSON = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => geoJSON });
</script>
