<script>import { onMount, onDestroy, setContext, getContext } from "svelte";
import { Polygon, Map } from "leaflet";
import { Compare } from "./utils/index";
export let latLngs;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let polygon;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = polygon;
onMount(() => {
  polygon = new Polygon(latLngs, options);
  compare = new Compare(polygon, $$props);
  ready = true;
});
$: if (map) {
  if (polygon) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(polygon);
    } else {
      map.addLayer(polygon);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  polygon?.remove();
  polygon = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => polygon });
</script>

{#if ready}
	<slot />
{/if}
