<script>import { onMount, onDestroy, setContext, getContext } from "svelte";
import { Polyline, Map } from "leaflet";
import { Compare } from "./utils/index";
export let latLngs;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let polyline;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = polyline;
onMount(() => {
  polyline = new Polyline(latLngs, options);
  compare = new Compare(polyline, $$props);
  ready = true;
});
$: if (map) {
  if (polyline) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(polyline);
    } else {
      map.addLayer(polyline);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  polyline?.remove();
  polyline = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => polyline });
</script>

{#if ready}
	<slot />
{/if}
