<script>import { onMount, onDestroy, getContext, setContext } from "svelte";
import { Rectangle, Map } from "leaflet";
import { Compare } from "./utils/index";
export let bounds;
export let options = {};
export let instance = void 0;
let parentContext = getContext(Map);
const { getMap, getLayer } = parentContext;
let rectangle;
let ready = false;
let compare;
$: map = getMap?.();
$: layer = getLayer?.();
$: instance = rectangle;
onMount(() => {
  rectangle = new Rectangle(bounds, options);
  compare = new Compare(rectangle, $$props);
  ready = true;
});
$: if (map) {
  if (rectangle) {
    compare.updateProps($$props);
    if (layer) {
      layer.addLayer(rectangle);
    } else {
      map.addLayer(rectangle);
    }
    compare.storeProps($$props);
  }
}
function reset() {
  rectangle?.remove();
  rectangle = void 0;
}
onDestroy(() => {
  reset();
});
setContext(Map, { ...parentContext, getOverlay: () => rectangle });
</script>

{#if ready}
	<slot />
{/if}
